package q1650;

import java.util.Arrays;
import org.junit.runner.RunWith;
import util.runner.Answer;
import util.runner.LeetCodeRunner;
import util.runner.TestData;
import util.runner.data.DataExpectation;

/**
 * [Medium] 1647. Minimum Deletions to Make Character Frequencies Unique
 * https://leetcode.com/problems/minimum-deletions-to-make-character-frequencies-unique/
 *
 * A string s is called good if there are no two different characters in s that have the same frequency.
 *
 * Given a string s, return the minimum number of characters you need to delete to make s good.
 *
 * The frequency of a character in a string is the number of times it appears in the string. For example, in the string
 * "aab", the frequency of 'a' is 2, while the frequency of 'b' is 1.
 *
 * Example 1:
 *
 * Input: s = "aab"
 * Output: 0
 * Explanation: s is already good.
 *
 * Example 2:
 *
 * Input: s = "aaabbbcc"
 * Output: 2
 * Explanation: You can delete two 'b's resulting in the good string "aaabcc".
 * Another way it to delete one 'b' and one 'c' resulting in the good string "aaabbc".
 *
 * Example 3:
 *
 * Input: s = "ceabaacb"
 * Output: 2
 * Explanation: You can delete both 'c's resulting in the good string "eabaab".
 * Note that we only care about characters that are still in the string at the end (i.e. frequency of 0 is ignored).
 *
 * Constraints:
 *
 * 1 <= s.length <= 10^5
 * s contains only lowercase English letters.
 */
@RunWith(LeetCodeRunner.class)
public class Q1647_MinimumDeletionsToMakeCharacterFrequenciesUnique {

    @Answer
    public int minDeletions(String s) {
        int[] counts = new int[26];
        for (int i = 0; i < s.length(); i++) {
            counts[s.charAt(i) - 'a']++;
        }
        Arrays.sort(counts);
        int res = 0, used = Integer.MAX_VALUE;
        for (int i = 25; i >= 0 && counts[i] > 0; i--) {
            if (used == 0) {
                res += counts[i];
            } else if (counts[i] >= used) {
                used--;
                res += counts[i] - used;
            } else {
                used = counts[i];
            }
        }
        return res;
    }

    @TestData
    public DataExpectation example1 = DataExpectation.create("aab").expect(0);

    @TestData
    public DataExpectation example2 = DataExpectation.create("aaabbbcc").expect(2);

    @TestData
    public DataExpectation example3 = DataExpectation.create("ceabaacb").expect(2);

    @TestData
    public DataExpectation normal1 = DataExpectation.create("bbcebab").expect(2);

    @TestData
    public DataExpectation normal2 = DataExpectation
            .create("")
            .expect(276);

}
